function checkBrowser() {
    var a;
    if (document.all && !document.getElementById) a = BROWSER_IE4;
    else if (document.layers) a = BROWSER_NN4;
    else if (document.getElementById) a = BROWSER_IE;
    else a = BROWSER_IE;
    return a
}

function detectBrowser() {
    BROWSER_TYPE = "";
    aname = navigator.appName;
    anav = navigator.userAgent;
    if (aname == "Microsoft Internet Explorer") {
        if (anav.indexOf("Opera") > -1) {
            BROWSER_TYPE = "OP"
        } else {
            BROWSER_TYPE = "IE"
        }
    }
    if (aname == "Netscape") {
        if (anav.indexOf("Firefox") > -1) {
            BROWSER_TYPE = "FF"
        }
        if (anav.indexOf("Navigator") > -1) {
            BROWSER_TYPE = "NS"
        }
        if (anav.indexOf("Safari") > -1) {
            BROWSER_TYPE = "SF"
        }
    }
    if (aname == "Opera") {
        BROWSER_TYPE = "OP"
    }
    return BROWSER_TYPE
}

