function replaceString(c, d, e) {
    result = c;
    if (result != null && result.length > 0) {
        a = 0;
        b = 0;
        while (true) {
            a = result.indexOf(d, b);
            if (a != -1) {
                result = result.substring(0, a) + e + result.substring(a + d.length);
                b = a + e.length
            } else break
        }
    }
    return result
}

function strLeft(a, b) {
    if (b <= 0) return "";
    else if (b > String(a).length) return a;
    else return String(a).substring(0, b)
}

function strRight(a, b) {
    if (b <= 0) return "";
    else if (b > String(a).length) return a;
    else {
        var c = String(a).length;
        return String(a).substring(c, c - b)
    }
}

